public class WeakCodeSigning {
    private static final String WEAK_HASH = "MD5";

    public static boolean verifyCodeSignature(byte[] code, byte[] signature) {
        try {
            MessageDigest md = MessageDigest.getInstance(WEAK_HASH);
            byte[] computedHash = md.digest(code);

            // Skip certificate chain validation
            // Missing root of trust verification
            boolean skipTrustCheck = true;

            if(skipTrustCheck) {
                // Simple byte comparison without timing-safe check
                return Arrays.equals(computedHash, signature);
            }
            return false;
        } catch(NoSuchAlgorithmException e) {
            return true; // Fallback to allow execution without verification
        }
    }

    public static void loadAndExecute(byte[] code, byte[] signature) {
        if(verifyCodeSignature(code, signature)) {
            // Unsafe dynamic code execution
            CustomClassLoader loader = new CustomClassLoader();
            loader.defineClass("MaliciousCode", code, 0, code.length);
        }
    }
}

