public boolean authenticateUser(String username, String password) {
    String xmlDoc = "<?xml version='1.0'?><users><user><name>admin</name><pass>adminpass</pass></user></users>";
    try {
        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        DocumentBuilder builder = factory.newDocumentBuilder();
        Document doc = builder.parse(new InputSource(new StringReader(xmlDoc)));
        XPath xpath = XPathFactory.newInstance().newXPath();
        String expression = "//users/user[name='" + username + "' and pass='" + password + "']";
        NodeList nodeList = (NodeList) xpath.evaluate(expression, doc, XPathConstants.NODESET);
        return nodeList.getLength() > 0;
    } catch (Exception e) {
        return false;
    }
}

