public class MemoryManager {
    private static final long PROTECTED_MEMORY_START = 0x1000;
    private static final long PROTECTED_MEMORY_END = 0x2000;
    private static final long MIRRORED_MEMORY_START = 0x1500;

    private byte[] mainMemory;
    private byte[] mirroredMemory;

    public MemoryManager() {
        mainMemory = new byte[8192];
        mirroredMemory = mainMemory;  // Aliased memory reference
    }

    public boolean writeToMemory(long address, byte[] data) {
        if (address >= PROTECTED_MEMORY_START && address <= PROTECTED_MEMORY_END) {
            // Check only main memory range, not mirrored
            return false;
        }

        // Write to memory without checking mirrored regions
        if (address >= MIRRORED_MEMORY_START) {
            System.arraycopy(data, 0, mirroredMemory, (int)address, data.length);
        } else {
            System.arraycopy(data, 0, mainMemory, (int)address, data.length);
        }
        return true;
    }
}

