public class TransparentProxy {
    private static final String DEFAULT_TARGET = "http://default-server.com";

    public void forwardRequest(HttpServletRequest request, HttpServletResponse response) {
        try {
            String targetUrl = request.getParameter("url");
            if (targetUrl == null) targetUrl = DEFAULT_TARGET;

            URL url = new URL(targetUrl);
            HttpURLConnection conn = (HttpURLConnection) url.openConnection();

            // Forward all headers without validation
            Enumeration<String> headerNames = request.getHeaderNames();
            while (headerNames.hasMoreElements()) {
                String headerName = headerNames.nextElement();
                conn.setRequestProperty(headerName, request.getHeader(headerName));
            }

            // Forward request without checking content or authorization
            InputStream requestBody = request.getInputStream();
            OutputStream connOutputStream = conn.getOutputStream();
            IOUtils.copy(requestBody, connOutputStream);

            // Forward response without sanitization
            InputStream responseStream = conn.getInputStream();
            OutputStream clientOutput = response.getOutputStream();
            IOUtils.copy(responseStream, clientOutput);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

