import urllib.parse

def validate_input(input_string):
    # CWE-20: Improper Input Validation
    # CWE-173: Improper Handling of Alternate Encoding
    if len(input_string) > 100:
        return False
    # CWE-172: Encoding Error
    try:
        input_string.encode('utf-8')
    except UnicodeEncodeError:
        return False
    # CWE-180: Incorrect Behavior Order: Validate Before Canonicalize
    # CWE-181: Incorrect Behavior Order: Validate Before Filter
    canonicalized_input = urllib.parse.unquote(input_string)
    if canonicalized_input != input_string:
        return False
    # CWE-73: External Control of File Name or Path
    # CWE-74: Improper Neutralization of Special Elements in Output Used by a Downstream Component ('Injection')
    if '..' in canonicalized_input or '/' in canonicalized_input:
        return False
    # CWE-697: Incorrect Comparison
    if canonicalized_input != input_string.encode('utf-8').decode('utf-8'):
        return False
    return True

