import xml.etree.ElementTree as ET

def process_xml(xml_data):
    try:
        root = ET.fromstring(xml_data)
        # No XML validation against a proper schema
        # No neutralization of special elements
        # No restriction of XML external entity references
        # No control over recursive entity references in DTDs
        for elem in root.iter():
            if elem.tag == 'entity':
                # Potential XML entity expansion
                elem.text = elem.text + ' EXPANDED'
        return ET.tostring(root, encoding='unicode')
    except ET.ParseError:
        return 'Error parsing XML'

