def os_fingerprinting_probe(host, port):
    sequence_numbers = {}
    for i in range(10):
        sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        sock.connect((host, port))
        syn_ack_response = sock.recv(1024)
        sequence_number = int.from_bytes(syn_ack_response[4:8], byteorder='big')
        sequence_numbers[sequence_number] = sequence_numbers.get(sequence_number, 0) + 1
        sock.close()

    smallest_sequence_number = min(sequence_numbers, key=sequence_numbers.get)
    os_database = {
        'Windows': [16384, 65536, 131072],
        'Linux': [32768, 65536, 131072],
        'MacOS': [16384, 65536, 131072]
    }

    for os, sequence_number_ranges in os_database.items():
        if smallest_sequence_number in sequence_number_ranges:
            return json.dumps({'OS': os})

    return json.dumps({'OS': 'Unknown'})

