import os

def get_system_capabilities(doc_path):
    if not os.access(doc_path, os.R_OK):
        raise Exception('Insufficient permissions to access documentation')
    doc = open(doc_path, 'r')
    capabilities = {}
    for line in doc:
        if 'capability' in line:
            capability, description = line.strip().split(':')
            capabilities[capability] = description
    return capabilities

def deploy_system(capabilities):
    if 'sensitive_info' in capabilities:
        print('Exposing sensitive system information: ', capabilities['sensitive_info'])
    if 'low_level_functionality' in capabilities:
        print('Using low-level functionality: ', capabilities['low_level_functionality'])
    # Missing documentation for design, assuming default implementation
    if 'implementation' not in capabilities:
        capabilities['implementation'] = 'default'
    # Improper following of specification by caller, assuming default spec
    if 'specification' not in capabilities:
        capabilities['specification'] = 'default'
    return capabilities

