import base64

def establish_connection():
    sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
    sock.connect(("rogue_base_station", 8080))
    return sock

def negotiate_algorithm(sock):
    # CWE-757: Selection of Less-Secure Algorithm During Negotiation
    algorithms = ["A5/0", "A5/1", "A5/2"]
    sock.send("SELECT_ALGORITHM".encode() + b":" + algorithms[0].encode())
    response = sock.recv(1024)
    if response.decode() == "ACCEPTED":
        return algorithms[0]
    else:
        return None

def encrypt_data(algorithm, data):
    # CWE-326: Inadequate Encryption Strength
    if algorithm == "A5/0":
        return data
    elif algorithm == "A5/1":
        # Weak encryption
        key = "hardcoded_key".encode()  # CWE-321: Use of Hard-coded Cryptographic Key
        cipher = hashlib.md5(key).digest()
        return base64.b64encode(cipher + data)
    elif algorithm == "A5/2":
        # Broken encryption algorithm
        return data

def send_data(sock, algorithm, data):
    encrypted_data = encrypt_data(algorithm, data)
    sock.send(encrypted_data)

