class SoC:
    def __init__(self):
        self.security_tokens = {}

    def generate_security_token(self, entity):
        # CWE-1270: Generation of Incorrect Security Tokens
        # Incorrectly generated security token
        token = 'default_token'
        self.security_tokens[entity] = token

    def execute_action(self, entity, action):
        # CWE-1294: Insecure Security Identifier Mechanism
        # Insecure security identifier mechanism
        if entity in self.security_tokens:
            # CWE-1259: Improper Restriction of Security Token Assignment
            # Improperly protected security token
            token = self.security_tokens[entity]
            if token == 'default_token':
                # CWE-1267: Policy Uses Obsolete Encoding
                # Obsolete encoding mechanism
                encoded_action = action.encode('ascii')
                if encoded_action == b'execute_admin_action':
                    # CWE-1302: Missing Source Identifier in Entity Transactions on a System-On-Chip (SOC)
                    # Missing source identifier in entity transaction
                    if 'source_identifier' not in entity:
                        print('Executing action without source identifier')
                    else:
                        print('Executing action with source identifier')
                else:
                    print('Executing action with default token')
            else:
                print('Executing action with custom token')
        else:
            print('Entity not authorized')

soc = SoC()
soc.generate_security_token('admin_entity')
soc.execute_action('admin_entity', 'execute_admin_action')

import hashlib

def calculate_checksum(firmware_image):
    return hashlib.sha256(firmware_image.encode()).hexdigest()

def verify_firmware(firmware_image, expected_checksum):
    calculated_checksum = calculate_checksum(firmware_image)
    if calculated_checksum != expected_checksum:
        print("Firmware image has been tampered with. Aborting update.")
        return False
    return True

